import React, { Component } from "react";
import { Redirect } from 'react-router';

export default class Update extends Component{
	constructor(props){
		super(props);
		this.state = {
			ID: '',
			Username: '',
			Password: ''
		};

		// this.submitHandler = this.submitHandler.bind(this);
		// this.changeHandler = this.changeHandler.bind(this);
		// this.test = this.test.bind(this);
	}

	componentDidMount(){
		fetch('http://localhost/demo-prosi-ci/index.php/websiterestcontroller/getUser/'+this.props.match.ID)
		.then(response => {
			return response.json();
		}).then(result => {
			console.log(result);
			this.setState({
				id:result.id,
				title:result.title,
				url:result.url
			});
		});	
	}

	test(){
		console.log('a');
	}

	render() {
		return (
			<form onSubmit={this.handleSubmit} method="POST">

				<h3>Updating Data</h3>

		        <div className="form-group">
		          <label className="d-block">Name:</label>
		          <input required type="text" className="form-control w-100" name="Username" value={this.state.Username} onChange={this.handleChange} />
		        </div>

		        <div className="form-group">
		          <label className="d-block">Password :</label>
		          <input required type="Password" className="form-control w-100" name="Password" value={this.state.Password} onChange={this.handleChange} />
		        </div>

		        <input className="d-block btn btn-info" type="submit" value="Submit" />
      		</form>
		)
	}
}
