import React, { Component } from "react";
import { Redirect } from 'react-router';
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";

export default class adduser extends Component{
	constructor(props) {
    super(props);
    this.state = {
      Username: '',
      Password: ''
    };
    this.adduser = this.adduser.bind(this);
  	}

  	handleChange(event){
  		const state = this.state;
  		state[event.target.name] = event.target.value;
  		this.setState(state);
  	}

  	handleSubmit(event) {
	  event.preventDefault();
	  fetch('http://localhost/demo-prosi-ci/index.php/websiterestcontroller/insertData', {
			method: 'POST',
			body: JSON.stringify({
				Username: this.state.title,
				Password: this.state.url
			}),
			headers: {
				"Content-type": "application/json; charset=UTF-8"
			}
		}).then(response => {
				if(response.status === 200) {
					alert("New website saved successfully");
				} else {
					alert("Error bang");
				}
			});
  }
	

  	addUser(event) {
		
	}


	render() {
		return (
			<div id="container">
				<Link to="/create">Add Website</Link>
				<p/>
				<table>
					<thead>
						<tr>
						{
							this.headers.map(function(h) {
								return (
									<th key = {h.key}>{h.label}</th>
								)
							})
						}
						  <th>Actions</th>
						</tr>
					</thead>
					<tbody>
						{
							this.state.users.map(function(item, key) {
							return (
								<tr key = {key}>
								  <td>{item.ID}</td>
								  <td>{item.Username}</td>
								  <td>{item.Password}</td>
								  <td>
										 
										<a href="javascript:void(0);" onClick={this.deleteUser.bind(this, item.ID)}>Delete</a>
								  </td>
								</tr>
											)
							}.bind(this))
						}
					</tbody>
				</table>
			</div>
		)
	}
}
