import React, { Component } from "react";
import { Redirect } from 'react-router';
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";

export default class Latihan extends Component {
  constructor(props) {
    super(props);
    this.state = {
      value: '',
      Username: '',
      Password: '',
      users: [],
      redirect: false
    };

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(event) {
    this.setState({Username: event.target.value});
  }

  handleSubmit(event) {
    event.preventDefault();
    fetch('http://localhost/demo-prosi-ci/index.php/websiterestcontroller/login', {
      method: 'POST',
      body: JSON.stringify({
        Username: this.state.Username,
        Password: this.state.Password
      }),
      headers: {
        "Content-type": "application/json; charset=UTF-8"
      }
    }).then(response => {
        if(response.status === 200) {
          alert("Berhasil login");
        } else {
          alert("Error bang "+this.state.Username+" "+this.state.Password);
        }
      });
  }

  render() {
      // if (this.state.redirect) {
      //   return <Redirect push to="/welcome" />;
      // }
    return (
      <form onSubmit={this.handleSubmit} method="POST">
        <div className="form-group">
          <label className="d-block">Name:</label>
          <input type="text" className="form-control w-100" value={this.state.Username} onChange={this.handleChange} />
        </div>

        <div className="form-group">
          <label className="d-block">Password :</label>
          <input type="text" className="form-control w-100" value={this.state.Password} onChange={this.handleChange} />
        </div>

        <input className="d-block btn btn-info" type="submit" value="Submit" />
      </form>
    );
  }
}
