import React, { Component } from "react";
import { Redirect } from 'react-router';
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";

export default class Latihan_2 extends Component{
	constructor(props) {
    super(props);
    this.state = {
      users: []
    };
    this.headers = [
			{ key: 'ID', label: 'ID'},
			{ key: 'Username', label: 'Username' },
			{ key: 'Password', label: 'Password' }
		];

    this.deleteUser = this.deleteUser.bind(this);
  }

  deleteUser(id) {
		if(window.confirm("Are you sure want to delete?")) {
			fetch('http://localhost/ci3-reactjs-crud/index.php/websiterestcontroller/deleteUser/' + id, {
	                method : 'DELETE'
				}).then(response => { 
					if(response.status === 200) {
						alert("User sudah dihapus");
                            fetch('http://localhost/ci3-reactjs-crud/index.php/websiterestcontroller/getAllUser')
						.then(response => {
							return response.json();
						}).then(result => {
							console.log(result);
							this.setState({
								users:result
							});
						});
					} 
			 });
		}
	}

	componentDidMount() {
		fetch('http://localhost/ci3-reactjs-crud/index.php/websiterestcontroller/getAllUser')
			.then(response => {
				return response.json();
			}).then(result => {
				console.log(result);
				this.setState({
					users:result
				});
			});
	}

	render() {
		return (
			<div id="container">
				<Link to="/create">Add Website</Link>
				<p/>
				<table>
					<thead>
						<tr>
						{
							this.headers.map(function(h) {
								return (
									<th key = {h.key}>{h.label}</th>
								)
							})
						}
						  <th>Actions</th>
						</tr>
					</thead>
					<tbody>
						{
							this.state.users.map(function(item, key) {
							return (
								<tr key = {key}>
								  <td>{item.ID}</td>
								  <td>{item.Username}</td>
								  <td>{item.Password}</td>
								  <td>
										<Link to={`/update/${item.id}`}>Edit</Link>
										 
										<a href="javascript:void(0);" onClick={this.deleteUser.bind(this, item.id)}>Delete</a>
								  </td>
								</tr>
											)
							}.bind(this))
						}
					</tbody>
				</table>
			</div>
		)
	}
}
