import React, { Component } from "react";
import { Redirect } from 'react-router';
import { BrowserRouter as Router, Switch, Route, Link } from "react-router-dom";

export default class Latihan extends Component {
  constructor(props) {
    super(props);
    this.state = {
      value: '',
      Username: '',
      Password: '',
      users: [],
      redirect: false
    };

    this.handleChange = this.handleChange.bind(this);
    this.handleSubmit = this.handleSubmit.bind(this);
  }

  handleChange(event) {
    this.setState({Username: event.target.value});
  }

  handleSubmit(event) {
    fetch('http://localhost/Demo-Prosi-ci/index.php/websiterestcontroller/login')
        .then(response => {
            return response.json();
        }).then(result => { 
            console.log(result);
            this.setState({
                users:result,
                redirect:true
            });
        });
    event.preventDefault();
  }

  render() {
      // if (this.state.redirect) {
      //   return <Redirect push to="/welcome" />;
      // }
    return (
      <form onSubmit={this.handleSubmit} method="POST">
        <label className="d-block">Name:</label>
        <input type="text" className="form-group" value={this.state.Username} onChange={this.handleChange} />

        <label className="d-block">Password :</label>
        <input type="text" className="form-group w-100" value={this.state.Password} onChange={this.handleChange} />

        <input className="d-block btn btn-info" type="submit" value="Submit" />
      </form>
    );
  }
}
